import chalk from 'chalk'

function log(...args) {
  return console.log(`▒ ${chalk.bold('guff')}`, ...args)
}

log.warn = (...args) =>
  console.warn(chalk.bgYellow.black(`▒`), chalk.bold('guff'), ...args)

log.success = (...args) =>
  console.log(chalk.bgGreen.black(`▒`), chalk.bold('guff'), ...args)

export default log
