import {client} from '../utils'


export function getSchemaNames (knex) {
  let {searchPath} = client(knex).config

  if (searchPath !== void 0) {
    searchPath = typeof searchPath === 'string' ? searchPath.split(',') : searchPath
    return searchPath.map(s => s.trim())
  }

  return ['public']
}


export default function getSchemaName (knex) {
  return getSchemaNames(knex)[0]
}
